import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_ENDPOINTS } from './config';

function CatListPage() {
  const [cats, setCats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCats = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await fetch(API_ENDPOINTS.GET_CATS, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          if (response.status === 401) {
            localStorage.removeItem('token');
            navigate('/login');
            return;
          }
          throw new Error('Failed to fetch cats');
        }

        const data = await response.json();
        setCats(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCats();
  }, [navigate]);

  const handleAddCat = () => {
    navigate('/cats/add');
  };

  const handleViewCat = (catId) => {
    navigate(`/cats/${catId}`);
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger" role="alert">
        {error}
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>My Cats</h2>
        <button className="btn btn-primary" onClick={handleAddCat}>
          Add Cat
        </button>
      </div>

      {cats.length === 0 ? (
        <div className="text-center">
          <p className="text-muted">You haven't added any cats yet</p>
          <button className="btn btn-primary" onClick={handleAddCat}>
            Add Your First Cat
          </button>
        </div>
      ) : (
        <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
          {cats.map((cat) => (
            <div key={cat.id} className="col">
              <div className="card h-100">
                <div className="card-body">
                  <h5 className="card-title">{cat.name}</h5>
                  <p className="card-text">
                    <strong>Breed:</strong> {cat.breed}<br />
                    <strong>Age:</strong> {cat.age} years<br />
                    <strong>Weight:</strong> {cat.weight}kg
                  </p>
                  <button
                    className="btn btn-outline-primary"
                    onClick={() => handleViewCat(cat.id)}
                  >
                    View Details
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CatListPage;
